import React, {useState} from "react"
import PropTypes from "prop-types";

function Card({ image, gif, title, content, tag, urlText }) {
    const [isHovered, setIsHovered] = useState(false);
    
    const handleMouseEnter = () => {
        setIsHovered(true);
    };
    
    const handleMouseLeave = () => {
        setIsHovered(false);
    };

    return <div className="card text-center rounded-0 shadow bg-gradient-dark">
        <picture>
            <img src={isHovered ? image : gif} alt="Poplloon Project" onMouseEnter={handleMouseEnter} onMouseDown={handleMouseLeave} />
        </picture>
        <div className="card-body">
            <h4 className="card-title">{title}</h4>
            <p className="card-text text-secundary">{content}</p>
            <a href="#!" className="btn btn-outline-secondary rounded-0">{urlText}</a>
            <blockquote className="mt-2 text-muted"><small>{tag}</small></blockquote>
        </div>
    </div>
}

Card.propTypes = {
    image: PropTypes.string,
    gif: PropTypes.string,
    title: PropTypes.string,
    content: PropTypes.string,
    urlText: PropTypes.string,
    tag: PropTypes.string
}

export default Card;
