import Card from "./Card";
import image2 from "../Assets/2.jpg";
import data from "../Assets/data.json";

const latestUpdate = data.data[0].latestUpdate;

function LineCardView() {

    console.log(latestUpdate);
    const cardData = [
        {
            id: 1,
            image: image2,
            primaryText: "Mira Softworks Website:",
            secundaryText: "a small website where I can to share you a lot of incredible talent.",
            tag: "#Web issues"
        },

        {
            id: 2,
            image: image1,
            primaryText: "Power Up has been replaced",
            secundaryText: "One of the power ups has been replaced as meaningless in the current core...",
            tag: "#Game dev"
        },

        {
            id: 3,
            image: image2,
            primaryText: "Mira Softworks Website:",
            secundaryText: "Lorem ipsum"
        },
    ]

    const cardData2 = [
        {
            id: 1,
            image: image2,
            primaryText: "Mochi le gustan los porros",
            secundaryText: "a small website where I can to share you a lot of incredible talent.",
            tag: "#Game dev"
        },

        {
            id: 2,
            image: image1,
            primaryText: "Power Up has been replaced",
            secundaryText: "One of the power ups has been replaced as meaningless in the current core...",
            tag: "#Game dev"
        },

        {
            id: 3,
            image: image2,
            primaryText: "Mira Softworks Website:",
            secundaryText: "Lorem ipsum"
        },
    ]

    return <div>
        <LineCardContent title="Latest Update" content={latestUpdate} />
    </div>
}

function LineCardContent({ title, content }) {
    return <div className="container d-flex justify-content-center align-items-center h-100 mt-5">
        <div className="row">
            <h1 className="mb-5 pt-5">- {title} -</h1>
            {
                content.map(cardDataSet => (
                    <div className="col-md-4" key={cardDataSet.id}>
                        <Card image={require(`${cardDataSet.image}`)} primaryText={cardDataSet.primaryText} secundaryText={cardDataSet.secundaryText} tag={cardDataSet.tag} />
                    </div>
                ))
            }
        </div>
    </div>
}

export default LineCardView;
