import PropTypes from "prop-types";
const srcPath = process.env.PUBLIC_URL;

function Card({ image, primaryText, secundaryText, tag}) {
    console.log(srcPath + image);

    return <div className="card text-center rounded-0 shadow bg-gradient-dark">
        <img src={srcPath + image} alt="Poplloon Project" />
        <div className="card-body">
            <h4 className="card-title">{primaryText}</h4>
            <p className="card-text text-secundary">{secundaryText}</p>
            <blockquote className="mt-2 text-muted"><small>{tag}</small></blockquote>
        </div>
    </div>
}

Card.propTypes = {
    image: PropTypes.string,
    primaryText: PropTypes.string,
    secundaryText: PropTypes.string,
    tag: PropTypes.string
}

export default Card;
