import Card from "../components/Card";
import CardOverlay from "../components/CardOverlay";

export function HomeCardContent({ title, contentData }) {
    return <div className="container d-flex justify-content-center align-items-center h-100 mt-5">
        <div className="row">
            <h1 className="mb-5 pt-5">- {title} -</h1>
            {
                contentData.map(contentSet => (
                    <div className="col-md-4" key={contentSet.id}>
                        <Card image={contentSet.staticImage} gif={contentSet.gifImage} title={contentSet.title} content={contentSet.content} urlText={contentSet.btnText} hRef={contentSet.url} tag={contentSet.tag} />
                    </div>
                ))
            }
        </div>
    </div>
}

export function ProjectCardContent({ title, contentData }) {
    return <div className="container d-flex justify-content-center align-items-center h-100 pt-5">
        <div className="col">
            <h1 className="mb-3 pt-5">- {title} -</h1>
            {
                contentData.map(contentSet => (
                    <div className="my-3" key={contentSet.id}>
                        <CardOverlay image={contentSet.image} title={contentSet.title} content={contentSet.content} date={contentSet.date} urlText={contentSet.urlText} url={contentSet.url} />
                    </div>
                ))
            }
        </div>
    </div>
}

export default NewsLayout();
