import Header from "./layouts/Header";
import HomeView from "./Views/HomeView";
import GalleryView from "./Views/GalleryView";
import "./Assets/stuff.css";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import NavBarFunction from "./layouts/NavBar";
import { Fragment } from "react";

function App() {
    return (
        <div className="App text-center">
            <div className="container d-flex">
                <Header style={{zIndex: "10", position: "relative"}} text="Mira Softworks is not only a personal brand, it is what identifies me as a 3D artist and video game developer, so it is also my little corner on the web where I can share everything I learn with you. I hope you have fun exploring my projects and capture my passion for what I do. I work hard to deliver innovative and creative solutions to the challenges that come my way and I'm excited to show you what I've accomplished so far." />
            </div>

            <BrowserRouter>
                <Routes>
                    <Route path="/" element={<NavBarFunction />}>
                        <Route index element={<HomeView />} />
                        <Route path="gallery" element={Fragment} />
                        <Route path="projects" element={Fragment} />
                        <Route path="about" element={Fragment} />

                        <Route path="*" element={<Navigate replace to="/" />} />
                    </Route>
                </Routes>
            </BrowserRouter>
        </div>
    )
}

export default App;
