import Header from "./layouts/Header";
import Footer from "./layouts/Footer"
import HomeView from "./Views/HomeView";
import GalleryView from "./Views/GalleryView";
import AboutView from "./Views/AboutView";
import "./Assets/stuff.css";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import { Fragment } from "react";

function App() {
    return (
        <div className="App text-center">
            <BrowserRouter>
                <Routes>
                    <Route path="/" element={<Header />}>
                        <Route index element={<HomeView />} />
                        <Route path="gallery" element={Fragment} />
                        <Route path="projects" element={Fragment} />
                        <Route path="about" element={<AboutView aboutText="Mira Softworks is not only a personal brand, it is what identifies me as a 3D artist and video game developer, so it is also my little corner on the web where I can share everything I learn with you. I hope you have fun exploring my projects and capture my passion for what I do. I work hard to deliver innovative and creative solutions to the challenges that come my way and I'm excited to show you what I've accomplished so far." paragraph1="Mi viaje en el desarrollo y modelado 3D comenzó hace años, cuando me fasciné por la posibilidad de dar vida a ideas y conceptos en entornos digitales. Con dedicación y perseverancia, he adquirido un amplio conocimiento en el uso de herramientas líderes en la industria, como Blender, GIMP, Unity, GIT, entre otros... Mi enfoque principal ha sido la creación de modelos 3D de alta calidad y realismo, ya sea para proyectos de animación, videojuegos, realidad virtual o arquitectura. He tenido el privilegio de colaborar con equipos multidisciplinarios en diversos proyectos, donde mi experiencia se ha vuelto crucial para llevar a cabo diseños impactantes y convincentes." />} />

                        <Route path="*" element={<Navigate replace to="/" />} />
                    </Route>
                </Routes>
            </BrowserRouter>

            <div>
                <Footer />
            </div>
        </div>
    )
}

export default App;
