import Card from "./Card";
import CardHeader from "./CardHeader";
import Data from "../Assets/Data/data.json"

const dataPath = Data.data[0].updateSection;

function LineCardView() {
    return <div>
        <LineCardContent title="Latest Update" contentData={dataPath} />
    </div>
}

function LineCardContent({ title, contentData }) {
    return <div className="container d-flex justify-contentData-center align-items-center h-100 mt-5">
        <div className="row">
            <h1 className="mb-5 pt-5">- {title} -</h1>
            {
                contentData.map(contentSet => (
                    <div className="col-md-4" key={contentSet.id}>
                        <Card>
                            <CardHeader srcPath={contentSet.media} title={contentSet.title} />
                            <CardBody content={contentSet.content} urlText="Check it" tag={contentSet.tag} />
                        </Card>
                    </div>
                ))
            }
        </div>
    </div>
}

export default LineCardView;
