import Data from "../Assets/Data/data.json";
import PropTypes from "prop-types";

const dataPath = Data.data[0].updateSection;

function LineCardView() {
    return <div>
        <LineCardContent title="Latest Update" contentData={dataPath} />
    </div>
}

function LineCardContent({ title, contentData }) {
    return <div className="container d-flex justify-contentData-center align-items-center h-100 mt-5">
        <div className="row">
            <h1 className="mb-5 pt-5">- {title} -</h1>
            {
                contentData.map(contentSet => (
                    <div className="col-md-4" key={contentSet.id}>
                        <Card image={contentData.pic} primaryText={contentSet.primaryText} secundaryText={contentSet.secundaryText} tag={contentSet.tag} />
                    </div>
                ))
            }
        </div>
    </div>
}

function Card({ image, primaryText, secundaryText, tag}) {
    console.log(primaryText);

    return <div className="card text-center rounded-0 shadow bg-gradient-dark">
        <img src={image} alt="Poplloon Project" />
        <div className="card-body">
            <h4 className="card-title">{primaryText}</h4>
            <p className="card-text text-secundary">{secundaryText}</p>
            <blockquote className="mt-2 text-muted"><small>{tag}</small></blockquote>
        </div>
    </div>
}

Card.propTypes = {
    image: PropTypes.string,
    primaryText: PropTypes.string,
    secundaryText: PropTypes.string,
    tag: PropTypes.string
}

export default LineCardView;
