import React from "react";
import { Button } from "react-bootstrap";
import ContactData from "../Assets/Data/dataContact.json";

const contactSet = ContactData.handleContactBody

function ContactForm() {
    const handleEmailClick = () => {
        const email = contactSet.email;
        const subject = contactSet.subject;
        const bodyText = contactSet.body;

        const mailToLink = `mailto:${email}?subject=${encodeURIComponent(subject)}&bodyText=${encodeURIComponent(bodyText)}`;
        
        window.location.href = mailToLink;
    }

    return <div className="text-center">
        <h1 className="mt-4">- Contact -</h1>

        <Button variant="dark" onClick={handleEmailClick}>
            Contact Me
        </Button>
    </div>
}

export default ContactForm
