import React, { useState } from "react";
import Collapse from "react-bootstrap/Collapse";
import "bootstrap/dist/css/bootstrap.min.css";
import AboutImage from "../Assets/Img/Albert.jpg";
import PropTypes from "prop-types";

function AboutView({ aboutText }) {
    const [open, setOpen] = useState(false);

    return <div>
        <div className="container d-flex">
            <h5 className="text-center mt-5 mb-5">{aboutText}</h5>
        </div>

        <div className="imgAbout mt-5 align-items-center text-center">
            <picture>
                <img className="img-fluid rounded-circle" src={AboutImage} alt="Albert About" width={350} height={360} onMouseEnter={() => setOpen} />
                <Collapse in={open}>
                    <div id="collapseID">

                    </div>
                </Collapse>
            </picture>
        </div>
    </div>
}

AboutView.propTypes = {
    aboutText: PropTypes.string
}

export default AboutView
